import React from 'react';
import './UserAddForm.css';
import 'profil.gif';
class UserAddForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            salary:'',
            email: '',
            image:'profil.gif',
            isGoldClient: false
        };
    }

    updateName(event) {
        this.setState({name: event.target.value});
    }

    updateEmail(event) {
        this.setState({email: event.target.value});
    }

    updateSalary(event){
        this.setState({salary:event.target.value});
    }

    updateIsGoldClient(event) {
        this.setState({isGoldClient: event.target.checked});
    }

    updateImage(event){
        this.setState({image:event.target.image});
    }
    

    render() {
        const {name, email,salary,image, isGoldClient} = this.state;

        return (
            <form
                className="user-add-form"
                onSubmit={(event) => this.props.submitAddForm(event, name, salary, email,image, isGoldClient)}
            >
                <h2>Adauga utilizatori:</h2>
                <label htmlFor="name">Nume:</label>
                <input
                    type="text"
                    name="name"
                    onChange={(event) => this.updateName(event)}
                />

                <label htmlFor="salary">Salariul:</label>
                <input
                    type="text"
                    salary="salary"
                    onChange={(event) => this.updateSalary(event)}
                />

                <label htmlFor="image">Imagine:</label>
                <input
                    type="image"
                    alt="Imagine indisponibila :("
                    image="image"
                    src="image"
                    onChange={(event) => this.updateImage(event)}
                />

                <label htmlFor="email">Email:</label>
                <input
                    type="text"
                    name="email"
                    onChange={(event) => this.updateEmail(event)}
                />
                <label htmlFor="is-gold-client">Client GOLD</label>
                <input
                    type="checkbox"
                    name="is-gold-client"
                    value="true"
                    onChange={(event) => this.updateIsGoldClient(event)}
                />

                <input type="submit" value="Introdu utilizatorul"/>
            </form>
        )
    }
}

export default UserAddForm;
