import React from 'react';
import UserList from './components/UserList';
import UserAddForm from './components/UserAddForm';
import './App.css';
import PostList from './components/PostList';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      background: 'white',
      font:'black',
      users: [],
      posts:[]
    };
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => response.json())
      .then(data => {
        data = data.filter(user => user.id < 4);
        data.forEach(user => {
          user.isGoldClient = false;
        });
        this.setState({users: data});
      })


      fetch('https://jsonplaceholder.typicode.com/posts')
      .then(response => response.json())
      .then(data => {
        data.filter(post => post.id <4);
        this.setState({posts:data});
      })



  }

  changeColor(event) {
    this.setState({background: event.target.value});
  }

  changeTextColor(event){
    this.setState({color:event.target.value});
  }

  getMaxId(users) {
    let maxId = 0;

    users.forEach(user => {
      if (user.id > maxId) {
        maxId = user.id;
      }
    });

    return maxId;
  }

  submitAddForm(event, name, email,salary,image, isGoldClient) {
    event.preventDefault();
    this.setState(prevState => {
      return {
        users: [
          ...prevState.users,
          {
            id: this.getMaxId(prevState.users) + 1,
            name,
            email,
            image,
            salary,
            isGoldClient
          }
        ]
      }
    });
  }

  render() {
    return(
      <div className="app" style={{background: this.state.background,color:this.state.color}}>
        <h1>Admin panel - Proiectul 1</h1>
        <UserAddForm submitAddForm={(event, name,salary, email,image, isGoldClient) => this.submitAddForm(event, name,salary, email,image, isGoldClient)}/>
        <UserList users={this.state.users}/>
        <PostList posts={this.state.users}/>
        <p>Schimba culoarea fundalului:</p>
        <input type="color" onChange={(event) => this.changeColor(event)}/>
        <p>Schimba culoarea fontului:</p>
        <input type="color" onChange={(event) => this.changeTextColor(event)}/>
      </div>
    );
  }
}

export default App;
